// This script creates the sound files for the Elite Token List

// Short function to convert base64 to Blob and save as file
function base64ToFile(base64, filename) {
  const byteString = atob(base64.split(',')[1]);
  const mimeString = base64.split(',')[0].split(':')[1].split(';')[0];
  
  const ab = new ArrayBuffer(byteString.length);
  const ia = new Uint8Array(ab);
  
  for (let i = 0; i < byteString.length; i++) {
    ia[i] = byteString.charCodeAt(i);
  }
  
  const blob = new Blob([ab], { type: mimeString });
  const link = document.createElement('a');
  link.href = URL.createObjectURL(blob);
  link.download = filename;
  link.click();
}

// Token Up Sound - Bright 'success' notification
// This is a short, bright ascending scale, good for positive price movements
// Base64 encoded MP3 file
const tokenUpSound = "data:audio/mp3;base64,";

// Token Down Sound - Descending tone
// This is a short, descending tone, suitable for negative price movements
// Base64 encoded MP3 file
const tokenDownSound = "data:audio/mp3;base64,";

// Rank Change Sound - Distinctive 'whoosh' sound
// This is a quick whooshing sound perfect for rank changes
// Base64 encoded MP3 file
const rankChangeSound = "data:audio/mp3;base64,";

// Script to save the sounds to disk
function saveSounds() {
  base64ToFile(tokenUpSound, 'token-up.mp3');
  base64ToFile(tokenDownSound, 'token-down.mp3');
  base64ToFile(rankChangeSound, 'rank-change.mp3');
  console.log('Sound files saved!');
}

